import { Component } from '@angular/core';

@Component({
  selector: 'inicio-secion',
  templateUrl: './inicio-secion.component.html',
  styleUrls: ['./inicio-secion.component.css']
})
export class InicioSecionComponent {

}

<form action="#">
    <div>
        <h2>Identificate</h2>
        <input name="correo" type="email" placeholder="E-mail*" class="email" required>
        <input type="password" name="pass" placeholder="Contraseña*" class="contraseña" minlength="8" required><br><br>
        <input name="Iniciar" value="Iniciar" type="submit" class="Iniciar">
    </div>
</form>
